package main.java;

public class Leetcode_5_longestPalindrome {
	

	public static void main(String[] args){
		String s = "cbbd";
		System.out.println(longestPalindrome(s));

	    s = "";
		System.out.println(longestPalindrome(s));

	    //"illi"
	    s = "iptmykvjanwiihepqhzupneckpzomgvzmyoybzfynybpfybngttozprjbupciuinpzryritfmyxyppxigitnemanreexcpwscvcwddnfjswgprabdggbgcillisyoskdodzlpbltefiz";
		System.out.println(longestPalindrome(s));

	}
	
	public static String findPalindrome(String s, int left, int right) {
		while (left >= 0 && right <= s.length()-1 && s.charAt(left)==s.charAt(right)) {
			left--;
			right++;
		}
		return s.substring(left+1, right);
	}
	
	public static String longestPalindrome(String s) {
		if (s.length() == 1) {
			return s;
		}
		
		String result = "";
		String tmp = "";
		
		for (int i=0; i<s.length(); i++) {
			tmp = findPalindrome(s, i, i);
			if (tmp.length() > result.length()) {
				result = tmp;
			}
			
			tmp = findPalindrome(s,  i, i+1);
			if (tmp.length() > result.length()) {
				result = tmp;
			}
		}
		return result;
		
	}
}
